const styles = theme => ({
    root: {
    },
    buttons: {
        width: 400,
        display: 'flex',
        flexDirection: 'row',
        justifyContent: "space-around",
        marginBottom: 10,
    },
    activeBtn: {
        '& .MuiButton-root': {
            backgroundColor: 'red'
        },
        '& .MuiButton-text': {
            color: 'green'
        }
    },
    inactiveBtn: {
        '& .MuiButton-root': {
            backgroundColor: 'green'
        },
        '& .MuiButton-text': {
            color: 'green'
        }
    }
  });
  export default styles;
