const styles = theme => ({
    container: {
        margin: "20px 20px 0px 20px",
        position: 'relative'
    },
    count: {
        position: 'absolute',
        width: 100,
        marginLeft: -50,
        marginTop: -25,
        top: "40%",
        left: "50%",
        textAlign: 'center',
        fontSize: 28,
        fontWeight: 600,
        color: theme.palette.text.main,
        lineHeight: 1.2
    }
  });
  export default styles;
