const styles = theme => ({
    root: {
        overflowY: "auto",
        minHeight: 420,
        maxHeight: 450,
        scrollbarWidth: 'none',
        '&::-webkit-scrollbar-thumb': {
            background: '#ffffff'
        },
        '&::-webkit-scrollbar-track': {
            '-webkit-box-shadow': 'inset 0 0 6px rgba(0,0,0,0.3)'
        },
        '&::-webkit-scrollbar-thumb': {
            '-webkit-box-shadow': 'inset 0 0 6px rgba(0,0,0,0.3)'
        },
        '&::-webkit-scrollbar-thumb': {
            backgroundColor: '#00ACED',
            borderRadius: 5
        }
    },
    table: {
        overflowY: "hidden"
    },
    itemContainer: {
        backgroundColor: theme.palette.primary.main,
        borderRadius: 25,
        padding: 10,
        marginBottom: 10,
        display: 'flex',
        flexDirection: "row",
        alignItems: 'center',
        maxWidth: 480,
        minWidth: 470,
        boxShadow: 
        `inset 5px 5px 7px #464452,
        inset -5px -5px 7px #6a667a`,
    },    
    itemThresh: {
        fontSize: 14,
        fontWeight: 500,
        color: theme.palette.text.main,
        marginleft: 5,
        marginRight: 10,
        width: 40,
        height: 40,
        lineHeight: 2.8,
        textAlign: "center",
        borderRadius: "50%"
    },
    itemText: {
        fontSize: 14,
        fontWeight: 300,
        color: theme.palette.text.main,
        width: "90%"
    }
  });
  export default styles;
