const styles = theme => ({
    root: {
        height: '100%',
        width: "100%",
        display: 'flex',
        flexDirection: "column",
        alignItems: 'center'
    },
    input: {
        marginTop: 50,
        '& label.Mui-focused': {
        color: theme.palette.text.main,
        },
        '& .MuiInputBase-input': {
            color: theme.palette.text.main,
        },
        '& .MuiFormLabel-root': {
            color: theme.palette.text.main,
        },
        '& .MuiOutlinedInput-root': {
            '& fieldset': {
                borderColor: theme.palette.text.main,
                borderRadius: 25,
            },
            '&:hover fieldset': {
                borderColor: theme.palette.text.main,
            },
            '&.Mui-focused fieldset': {
                borderColor: theme.palette.text.main,
            },
        },
    },
    field: {
        width: 600,
    },
    button: {
        marginLeft: 10,
        marginTop: 8
    },
    searchPane: {
        display: 'flex',
        flexDirection: 'row'
    },
    container: {
        height: 500,
        width: 400,
        display: 'flex',
        flexDirection: "column",
        alignItems: 'center',
        margin: "50px 20px 50px 20px",
        padding: 30,
        paddingTop: 50,
        borderRadius: 25,
        overflowY: "auto",
        backgroundColor: theme.palette.secondary.main
        
    }
  });
  export default styles;
