const styles = theme => ({
    container: {
        margin: "20px 20px 0px 20px",
        backgroundColor: 'red',
        position: 'relative'
    },
    count: {
        position: 'absolute',
        width: 100,
        height: 50,
        marginLeft: -50,
        marginTop: -25,
        top: "45%",
        left: "50%",
        textAlign: 'center',
        fontSize: 24,
        fontWeight: 600,
        color: theme.palette.text.main
    }
  });
  export default styles;
