const styles = theme => ({
    root: {
    },
    buttonsA: {
        width: 400,
        display: 'flex',
        flexDirection: 'row',
        justifyContent: "space-around",
        marginBottom: 20,
        '& .MuiButton-text': {
            color: theme.palette.text.main
        }
    },
    buttons: {
        cursor: "pointer",
        transition: "all .2s ease-in-out",
        "&:hover": {
          transform: "scale(1.05)",
          filter: "brightness(.8)"
        },
        "&:active": {
            transform: "scale(.95)",
          },
        '& .MuiButton-root': {
            backgroundColor: theme.palette.accent.main,
            borderRadius: 12,
            height: 36,
            width: "auto",
        },
        '& .MuiButton-text': {
            color: theme.palette.text.main
        },
        '& .Mui-disabled': {
            backgroundColor: theme.palette.secondary.disabled
        },
    },
    activeBtn: {
        '& .MuiButton-root': {
            backgroundColor: theme.palette.primary.main
        },
        '& .MuiButton-text': {
            color: theme.palette.text.main
        }
    }
  });
  export default styles;
