import { createMuiTheme } from '@material-ui/core/styles'


// A custom theme for this app
// To adjust hamburger icon, use if statement in Drawer.js
const theme = createMuiTheme({
  typography: {
    useNextVariants: true,
  },
  palette: {
    // Dark
    primary: {
      main: '#1D1D1D',
      bkg: '#1D1D1D66'
    },
    secondary: {
      main: '#424242'
    },
    text: {
      main: '#E9E9E9'
    },
  },
})

export default theme;
