import { React, useState, useEffect } from "react";
import axios from "axios";
import { withStyles } from "@material-ui/core/styles";
import { Typography, TextField, Button, Divider } from "@material-ui/core";
import { motion } from "framer-motion"

// Local imports
import Display from "components/Display/display"
import LoadingSkeleton from "components/Skeleton/skeleton"
import styles from "./styles.js";

const Home = (props) => {  
    const { classes } = props;

    const [Query, setQuery] = useState("");
    const [PrevQuery, setPrevQuery] = useState("");

    const [Filter, setFilter] = useState("Positive");
    const [Sentiment, setSentiment] = useState("");
    const [pieData, setPieData] = useState([]);
    const [tweetList, setTweetList] = useState([]);

    const [loaded, setLoaded] = useState(false);
    const [loading, setLoading] = useState(false);
    const [chartLoad, setChartLoad] = useState(false);

    const [params, setParams] = useState([{
      verified: false,
      retweet: false,
      exact: false,
      hashtags: false
    }])
  

    const handleFilter = (value) => {
      setFilter(value);
    }

    const handleParams = (input) => {

    }

  
    const handleSubmit = (event) => {
      console.log("here")
      console.log(Query)
      if (Query !== "")
      {
        // string.charAt(0).toUpperCase() + string.slice(1);
        setLoaded(false);
        setLoading(true);
        setChartLoad(false);
        setFilter("Positive");
        setPrevQuery(Query.charAt(0).toUpperCase() + Query.slice(1));
        setQuery("");
        setTimeout(() => {fetchTweets();}, 500)
        setTimeout(() => {setChartLoad(true);}, 1000)
      }   
      event.preventDefault();
    };

    const fetchTweets = () => {
      axios.get('/api/getTweets', { params:{ 
        keyword: Query,
        isConfirmed: false,
      }})
      .then((response) => {
        let data = response.data;
        setLoading(false);
        setLoaded(true);
        setSentiment(data[0]);
        setPieData(data[1]);
        setTweetList(data[2]);
      }).catch((error) => {
        console.error(error);
      });
    }


    return (
      <div className={classes.root}>
        <Typography className={classes.title}>Welcome to the Twitter Sentiment Tool</Typography>
        <Typography className={classes.desc}>Enter a topic to find the overall sentiment on Twitter.</Typography>
        <motion.div className={classes.activeContainer}>
            <form className={classes.input} noValidate autoComplete="off" onSubmit={handleSubmit}>
              <motion.div layout className={classes.inputRow}>
                <TextField 
                    className={classes.field}
                    label="Search"
                    variant="outlined"
                    value={Query}
                    onChange={(event) => setQuery(event.target.value)}
                />
                <div className={classes.submitButton}>
                  <Button type="Submit">Submit</Button>
                </div>
              </motion.div>
            </form>
            {loading && <LoadingSkeleton /> }
            {loaded && <Display chartLoad={chartLoad} prevQuery={PrevQuery} sentiment={Sentiment} filter={Filter} tweetList={tweetList} onChange={handleFilter} pieData={pieData}/> }
        </motion.div>
      </div>
    );
}

export default withStyles(styles)(Home);
