import { React, useState } from "react";
import { withStyles } from "@material-ui/core/styles";
import { Typography, TextField, Button } from "@material-ui/core";
import { PieChart, Pie, Cell, Tooltip } from "recharts";
import styles from "./styles.js";
import PieChartComp from "../../components/PieChart/piechart"

// [positive, neutral, negative]
const COLORS = ["#81C784", "#29B6F6", "#EF5350"];

const Home = (props) => {
    const [Query, setQuery] = useState("");
    const [Sentiment, setSentiment] = useState("");
    const [pieData, setPieData] = useState([])
    const [loaded, setLoaded] = useState(false)
  
    const handleSubmit = () => {
      if (Query !== "")
      {
         fetchTweets();
      }   
    };

    const fetchTweets = () => {
      fetch('/api/getTweets?query=' + Query)
      .then(res => res.json())
      .then(data => {
        console.log(data);
        setLoaded(true);
        setSentiment(data[0]);
        setPieData(data[1]);
      })
    }



    const { classes } = props;
    return (
      <div className={classes.root}>
        <Typography>Welcome to the Twitter Sentiment Tool</Typography>
        <form className={classes.input} noValidate autoComplete="off">
            <TextField 
                className={classes.field}
                label="Search"
                variant="outlined"
                value={Query}
                onChange={event => setQuery(event.target.value)}
            />
            <Button className={classes.button} onClick={() => handleSubmit()} variant="contained">Submit</Button>
        </form>
        {loaded &&

          <PieChartComp data={pieData} />
        }
        
        {Sentiment}
      </div>
    );
}

export default withStyles(styles)(Home);
