const styles = theme => ({
    root: {
        height: '100%',
        width: "100%",
        display: 'flex',
        flexDirection: "column",
        alignItems: 'center'
    },
    activeContainer: {
        backgroundColor: theme.palette.secondary.main,
        padding: 10,
        borderRadius: 25,
        display: 'flex',
        flexDirection: "column",
        alignItems: 'center'
    },
    title: {
        fontSize: 32,
        fontWeight: 600,
        color: theme.palette.text.main,
        marginTop: 36
    },
    desc: {
        fontSize: 18,
        fontWeight: 300,
        color: theme.palette.text.main,
        marginTop: 8,
        marginBottom: 40
    },
    input: {
        margin: 20,
        height: 56,
        '& label.Mui-focused': {
        color: theme.palette.text.main,
        },
        '& .MuiInputBase-input': {
            color: theme.palette.text.main,
        },
        '& .MuiFormLabel-root': {
            color: theme.palette.text.main,
        },
        '& .MuiOutlinedInput-root': {
            '& fieldset': {
                borderColor: theme.palette.text.main,
                borderRadius: 25,
            },
            '&:hover fieldset': {
                borderColor: theme.palette.text.main,
            },
            '&.Mui-focused fieldset': {
                borderColor: theme.palette.text.main,
            },
        },
    },
    field: {
        width: 600,
    },
    button: {
        marginLeft: 10,
        marginTop: 8
    },
    searchPane: {
        display: 'flex',
        flexDirection: 'row',
        width: 980,
        '& .MuiSkeleton-root': {
            marginTop: 400
        }
    },
    searchPaneLoading: {
        display: 'flex',
        flexDirection: 'row',
        width: 980,
        height: 530,
        justifyContent: "space-between"
    },
    loadingTable: {
        '& .MuiSkeleton-rect': {
            margin: 20,
            borderRadius: 20
        }
    },
    details: {
        color: theme.palette.text.main,
        fontSize: 18
    },
    tableContainer: {
        height: 470,
        width: 500,
        display: 'flex',
        flexDirection: "column",
        alignItems: 'center',
        margin: "0px 20px 50px 10px",
        padding: "10px 0px 10px 0px",
        borderRadius: 25,
    },
    dataTable: {
        overflowY: "auto",
        paddingRight: 2
    },
    buttons: {
        width: 400,
        display: 'flex',
        flexDirection: 'row',
        justifyContent: "space-around",
        marginBottom: 10
    }
  });
  export default styles;
