const styles = theme => ({
    root: {
        height: '100%',
        width: "100%",
        display: 'flex',
        flexDirection: "column",
        alignItems: 'center'
    },
    table: {
        height: 500,
        width: 400,
        margin: "50px 20px 50px 20px",
        padding: 20,
        borderRadius: 25,
        overflowY: "auto",
        backgroundColor: theme.palette.secondary.main
    },
    item: {
        fontSize: 16,
        fontWeight: 500,
        marginBottom: 20,
        color: theme.palette.text.main
    }
  });
  export default styles;
