import { createMuiTheme } from '@material-ui/core/styles'

// A custom theme for this app
const theme = createMuiTheme({
  typography: {
    useNextVariants: true,
  }, 
  palette: {
    primary: {
      // dark
      main: '#585566',
    },
    secondary: {
      // dark gray
      main: "#585566",
      tint: "#ACAAB3",
      disabled: "#42404D",
      divider: "rgba(255, 255, 255, 1)"
    },
    accent: {
      // dark purple
      main: '#42404D'
    },
    text: {
      // light
      main: '#ECF0F1',
      // gray
      alt: "#B3B6B7"
    }
  },
})

export default theme;
