const styles = theme => ({
    root: {
        height: '100vh',
    },
    input: {
        marginTop: 50,
        '& label.Mui-focused': {
        color: theme.palette.text.main,
        },
        '& .MuiInputBase-input': {
            color: theme.palette.text.main,
        },
        '& .MuiFormLabel-root': {
            color: theme.palette.text.main,
        },
        '& .MuiOutlinedInput-root': {
            '& fieldset': {
                borderColor: theme.palette.text.main,
                borderRadius: 25,
            },
            '&:hover fieldset': {
                borderColor: theme.palette.text.main,
            },
            '&.Mui-focused fieldset': {
                borderColor: theme.palette.text.main,
            },
        },
    },
    field: {
        width: 600,
    },
    button: {
        marginLeft: 10,
        marginTop: 8
    },
  });
  export default styles;
