import { React, useState } from "react";
import { withStyles } from "@material-ui/core/styles";
import { Typography, TextField, Button } from "@material-ui/core";
import styles from "./styles.js";
import firebase from "../../firebase/firebase";

const Home = (props) => {
    const [Query, setQuery] = useState("");
    const [Sentiment, setSentiment] = useState("");
    const [Text, setText] = useState("");
    const { classes } = props;
    let arrayf = []

    const handleSubmit = () => {
        let test;
        /*
        Main.main("trump").then((response) => {
          console.log(response);
          let array = response[0];
          
          arrayf = response[0];
          console.log(arrayf);
          setText(arrayf[0].text);
          setSentiment(response[1]);
          
        });
        */
        const sayHello = firebase.functions().httpsCallable('runAnal');
        sayHello();
        //console.log(test);
      };

    return (
      <div className={classes.root}>
        <Typography>Hello World!</Typography>
        <form className={classes.input} noValidate autoComplete="off">
            <TextField 
                className={classes.field}
                label="Search"
                variant="outlined"
                value={Query}
                onChange={event => setQuery(event.target.value)}
            />
            <Button className={classes.button} onClick={() => handleSubmit()} variant="contained">Submit</Button>
        </form>
        {Query}
        {Sentiment}
        {Text}
   
      </div>
    );
}

export default withStyles(styles)(Home);
