const styles = theme => ({
    table: {
      overflowX: "hidden",
      "::-webkit-scrollbar": {
        width: "12px"
        },
        "::-webkit-scrollbar-track": {
            "-webkit-box-shadow": "inset 0 0 6px     rgba(0,0,0,0.3)",
            borderRadius: "10px"
        },
        "::-webkit-scrollbar-thumb": {
            borderRadius: 10,
            "-webkit-box-shadow": "inset 0 0 6px     rgba(0,0,0,0.5)"
        }
    },
    itemContainer: {
        backgroundColor: theme.palette.primary.main,
        borderRadius: 25,
        padding: 10,
        marginBottom: 10,
        display: 'flex',
        flexDirection: "row",
        alignItems: 'center'
    },    
    itemThresh: {
        fontSize: 14,
        fontWeight: 500,
        color: theme.palette.text.main,
        marginLeft: 5,
        width: "10%"
    },
    itemText: {
        fontSize: 14,
        fontWeight: 300,
        color: theme.palette.text.main,
        width: "90%"
    }
  });
  export default styles;
